import React from 'react'

class Currenttime extends React.Component{
	constructor(){
		super();
		this.state={
			currentDateTime:"",
			dayOfTheWeek:"",
			timeZoneName:""
		};
	}
	componentDidMount(){
		const api = fetch(`http://worldclockapi.com/api/json/utc/now`);
		const response= api.json();
		this.setState({
			currentDateTime:response.currentDateTime,
			dayOfTheWeek:response.dayOfTheWeek,
			timeZoneName:response.timeZoneName
		});
	}
    render(){
		return(
			<div>
				<h2 className="tc ma2 br f2">Current Date and Time</h2>
				<p>{this.state.currentDateTime}</p>
				<p>{this.state.dayOfTheWeek}</p>
				<p>{this.state.timeZoneName}</p>
			</div>

		)
	}
}
export default Currenttime;
