import React,{Component} from 'react'

class Signin extends Component {
constructor(props)
{
	super(props);
	this.state = {
				  userProfile:{}
		}
}
makeApiCall = () => {
	let emaill=document.getElementById('email-address').value;
     fetch(`http://apilayer.net/api/check?access_key=8fd1da419cb38c4883df942c99a274b4&email=${emaill}`)
    .then(response => response.json())
    .then(data => {
    		       this.setState({userProfile: data})});
       //this.handle();
    }
handle = () => {
	const {onRouteChange} = this.props;
	this.state.mx_found ? onRouteChange('Home') : alert(console.log(this.state.user));
}
render(){
	return(
		<article className="br2 ba dark-gray b--black-10 mv4 w-100 w-50-m w-25-l mw5 center">
		<main className="pa4 black-80">
		  <form className="measure">
		    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
	      	<legend className="f4 fw6 ph0 mh0">Sign In</legend>
		    <div className="mt3">
		       <label className="db fw6 lh-copy f6" fo="email-address">Email</label>
		        <input 
		           className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
		           type="email" name="email-address"
		           id="email-address" 
		         />
		      </div>
		      <div className="mv3">
		        <label className="db fw6 lh-copy f6" fo="password">Password</label>
		        <input
		             className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
		             type="password" 
		             name="password"  
		             id="password" 
		         />
		      </div>
		    </fieldset>
		    <div className="">
		        <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
		               type="submit" 
		               value="Sign in"
		               onClick={this.makeApiCall}
		         />
		    </div>
		    <div className="lh-copy mt3">
	      <a href="#0" className="f6 link dim black db">Sign up</a>
	    </div>
	  </form>
	</main>
	</article>
  )
}
}
export default Signin;
